import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function CreateStudent() {
  const [student, setStudent] = useState({
    id: '',
    name: '',
    place: '',
    phone: ''
  });
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation
    if (!student.id || !student.name || !student.place || !student.phone) {
      alert("All fields are required.");
      return;
    }

    // Ensure ID is a number
    const studentData = { ...student, id: Number(student.id) };

    fetch('http://localhost:8000/students', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(studentData)
    }).then(() => {
      navigate('/');
    });
  };

  return (
    <div className="container">
      <h2>Add New Student</h2>
      <form onSubmit={handleSubmit} className="form-style">
        <input
          type="number"
          placeholder="ID"
          value={student.id}
          onChange={(e) => setStudent({ ...student, id: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Name"
          value={student.name}
          onChange={(e) => setStudent({ ...student, name: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Place"
          value={student.place}
          onChange={(e) => setStudent({ ...student, place: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Phone"
          value={student.phone}
          onChange={(e) => setStudent({ ...student, phone: e.target.value })}
          required
        />
        <button type="submit" className="btn btn-add">Add Student</button>
      </form>
    </div>
  );
}
