import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

export default function CreateStudent() {
  const [student, setStudent] = useState({ id: '', name: '', place: '', phone: '' });
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:8000/students', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    name: student.name,
    place: student.place,
    phone: student.phone,
  }),
})  
  };

  return (
    <div className="container">
      <h2>Add New Student</h2>
      <form onSubmit={handleSubmit} className="form-style">
        <input type="text" placeholder="ID" value={student.id} onChange={(e) => setStudent({ ...student, id: e.target.value })} />
        <input type="text" placeholder="Name" value={student.name} onChange={(e) => setStudent({ ...student, name: e.target.value })} />
        <input type="text" placeholder="Place" value={student.place} onChange={(e) => setStudent({ ...student, place: e.target.value })} />
        <input type="text" placeholder="Phone" value={student.phone} onChange={(e) => setStudent({ ...student, phone: e.target.value })} />
        <button type="submit" className="btn btn-primary">Add</button>
      </form>
    </div>
  );
}
