import { useParams, Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

export default function ViewDetails() {
  const { id } = useParams();
  const [student, setStudent] = useState(null);
  const [notFound, setNotFound] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Use query param to match string ID
    fetch(`http://localhost:8000/students?id=${id}`)
      .then((res) => {
        if (!res.ok) {
          setNotFound(true);
          setLoading(false);
          return null;
        }
        return res.json();
      })
      .then((data) => {
        if (data && data.length > 0) {
          setStudent(data[0]); // Get the first (only) match
        } else {
          setNotFound(true);
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setNotFound(true);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (notFound) return <div style={{ color: 'red' }}>Student not found.</div>;

  return (
    <div className="container">
      <h2>Student Details</h2>
      <div
        className="details-box"
        style={{
          textAlign: 'left',
          padding: '20px',
          border: '1px solid #ccc',
          borderRadius: '8px',
          backgroundColor: '#f8f8fc',
        }}
      >
        <p><strong>ID:</strong> {student.id}</p>
        <p><strong>Name:</strong> {student.name}</p>
        <p><strong>Place:</strong> {student.place}</p>
        <p><strong>Phone:</strong> {student.phone}</p>
        <Link to="/" className="btn btn-add">Back to List</Link>
      </div>
    </div>
  );
}
