import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

export default function EditStudent() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [student, setStudent] = useState({
    id: '',
    name: '',
    place: '',
    phone: '',
  });
  const [errors, setErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:8000/students/${id}`)
      .then((res) => res.json())
      .then((data) => setStudent(data))
      .catch((err) => setErrors({ fetch: 'Failed to fetch student data.' }));
  }, [id]);

  const validate = () => {
    const errs = {};
    if (!student.id.trim()) errs.id = "ID is required";
    if (!student.name.trim()) errs.name = "Name is required";
    if (!student.place.trim()) errs.place = "Place is required";
    if (!student.phone.trim()) {
      errs.phone = "Phone is required";
    } else if (!/^\d{10}$/.test(student.phone)) {
      errs.phone = "Phone must be 10 digits";
    }
    setErrors(errs);
    return Object.keys(errs).length === 0;
  };

  const handleChange = (e) => {
    setStudent({ ...student, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: undefined });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validate()) return;
    setSubmitting(true);
    fetch(`http://localhost:8000/students/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(student),
    })
      .then(() => {
        setSuccess(true);
        setTimeout(() => navigate('/'), 1000);
      })
      .catch(() => {
        setErrors({ submit: "Failed to update student. Try again!" });
        setSubmitting(false);
      });
  };

  return (
    <div className="form-container" style={{ maxWidth: 400, margin: "2rem auto", padding: 20, border: "1px solid #eee", borderRadius: 8 }}>
      <h2>Edit Student</h2>
      {errors.fetch && <div style={{ color: "red", marginBottom: 8 }}>{errors.fetch}</div>}
      <form onSubmit={handleSubmit} autoComplete="off">
        <div style={{ marginBottom: 12 }}>
          <label htmlFor="id">ID</label>
          <input name="id" id="id" value={student.id} onChange={handleChange} placeholder="Id" required />
          {errors.id && <div style={{ color: "red", fontSize: 12 }}>{errors.id}</div>}
        </div>
        <div style={{ marginBottom: 12 }}>
          <label htmlFor="name">Name</label>
          <input name="name" id="name" value={student.name} onChange={handleChange} placeholder="Name" required />
          {errors.name && <div style={{ color: "red", fontSize: 12 }}>{errors.name}</div>}
        </div>
        <div style={{ marginBottom: 12 }}>
          <label htmlFor="place">Place</label>
          <input name="place" id="place" value={student.place} onChange={handleChange} placeholder="Place" required />
          {errors.place && <div style={{ color: "red", fontSize: 12 }}>{errors.place}</div>}
        </div>
        <div style={{ marginBottom: 12 }}>
          <label htmlFor="phone">Phone</label>
          <input name="phone" id="phone" value={student.phone} onChange={handleChange} placeholder="Phone" required />
          {errors.phone && <div style={{ color: "red", fontSize: 12 }}>{errors.phone}</div>}
        </div>
        <button type="submit" disabled={submitting} className="btn btn-primary">
          {submitting ? "Updating..." : "Update"}
        </button>
        {errors.submit && <div style={{ color: "red", marginTop: 8 }}>{errors.submit}</div>}
        {success && <div style={{ color: "green", marginTop: 8 }}>Student updated! Redirecting...</div>}
      </form>
    </div>
  );
}
