import { useParams, Link } from 'react-router-dom';
import { useEffect, useState } from 'react';

export default function ViewDetails() {
  const { id } = useParams();
  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:8000/students/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error('Student not found');
        return res.json();
      })
      .then((data) => {
        setStudent(data);
        setError(null);
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <div style={{ textAlign: "center", marginTop: 40 }}>Loading...</div>;
  if (error) return (
    <div style={{ color: "red", textAlign: "center", marginTop: 40 }}>
      {error} <br />
      <Link to="/" className="btn" style={{ marginTop: 20, display: "inline-block" }}>Back to List</Link>
    </div>
  );

  return (
    <div 
      className="view-details"
      style={{
        maxWidth: 400,
        margin: "2rem auto",
        padding: 24,
        border: "1px solid #eee",
        borderRadius: 10,
        background: "#fafaff",
        boxShadow: "0 2px 8px #eee"
      }}
    >
      <h2 style={{ color: "#5a3ea1", marginBottom: 24 }}>Student Details</h2>
      <dl>
        <dt style={{ fontWeight: "bold" }}>ID:</dt>
        <dd style={{ marginBottom: 12 }}>{student.id}</dd>
        <dt style={{ fontWeight: "bold" }}>Name:</dt>
        <dd style={{ marginBottom: 12 }}>{student.name}</dd>
        <dt style={{ fontWeight: "bold" }}>Place:</dt>
        <dd style={{ marginBottom: 12 }}>{student.place}</dd>
        <dt style={{ fontWeight: "bold" }}>Phone:</dt>
        <dd style={{ marginBottom: 12 }}>{student.phone}</dd>
      </dl>
      <div style={{ display: "flex", gap: 10, marginTop: 24 }}>
        <Link to="/" className="btn btn-secondary" style={{ flex: 1, textAlign: "center" }}>Back to List</Link>
        <Link to={`/edit/${student.id}`} className="btn btn-primary" style={{ flex: 1, textAlign: "center" }}>Edit</Link>
      </div>
    </div>
  );
}
